// src/components/Navbar.js

import React, { useState } from 'react';
import "../assets/css/Product.css";

const Product = () => {
  const [isCategoryOpen, setIsCategoryOpen] = useState(false);
  const [isSubCategoryOpen, setIsSubCategoryOpen] = useState(false);

  const handleCategoryMouseEnter = () => {
    setIsCategoryOpen(true);
  };

  const handleCategoryMouseLeave = () => {
    setIsCategoryOpen(false);
  };

  return (
    <>

      <div className="navbar-menu">
        <div className="navbar-start">
          <div  className="navbar-item has-dropdown" onMouseEnter={handleCategoryMouseEnter} onMouseLeave={handleCategoryMouseLeave} >
            
            <a className="navbar-link">Home</a>
            {isCategoryOpen && (
              <div className="navbar-dropdown">
              
                <a className="navbar-item" href="/fashion">Home Style 01</a>
                <a className="navbar-item" href="/fashion">Home Style 02</a>
                <a className="navbar-item" href="/home">Home Style 03</a>
              </div>
            )}
          </div>

        </div>
      </div>

                  
    
    </>
  );
};

export default Product;
