// src/components/Navbar.js

import React, { useState } from 'react';
import "../assets/css/Product.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import fakeData from "../MOCK_DATA.json";
import { useTable } from "react-table";

const Product = () => {
  const caroselImages = [
    {
        img: 'https://avon-demo.myshopify.com/cdn/shop/files/collection-list-1-demo4_300x.jpg?v=1614330616',
        discription: 'MINI DRESS',
        price: "$300.00",
    },
    {
        img: 'https://avon-demo.myshopify.com/cdn/shop/files/women-shoes_300x.jpg?v=1614330616',
        discription: 'SHOES',
        price: "$300.00",
    },
    {
        img: 'https://avon-demo.myshopify.com/cdn/shop/files/mini-skirt_300x.jpg?v=1614331484',
        discription: 'MINI SKIRTS',
        price: "$300.00",
    },
    {
        img: 'https://avon-demo.myshopify.com/cdn/shop/files/collection-list-6-demo4_300x.jpg?v=1614330616',
        discription: 'SUNGLASSES',
        price: "$300.00",
    },
    {
        img: 'https://avon-demo.myshopify.com/cdn/shop/files/tops-new_300x.jpg?v=1614331484',
        discription: 'TOPS',
        price: "$300.00",
    },
    {
        img: 'https://avon-demo.myshopify.com/cdn/shop/files/tops_c89b3b8d-0b69-4c14-9b72-bb83e63354bc_300x.jpg?v=1614331484',
        discription: 'NEW Arrivals',
        price: "$300.00",
    }


]
const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 1,
};

    // ----Table-----
    const data = React.useMemo(() => fakeData, []);
    const columns = React.useMemo(
      () => [
        {
          Header: "Size",
          accessor: "size",
        },
        {
          Header: "XXS-Xs",
          accessor: "xxs-xs",
        },
        {
          Header: "XS-S",
          accessor: "xs-s",
        },
        {
          Header: "S-M",
          accessor: "s-m",
        },
        {
          Header: "M-L",
          accessor: "m-l",
        },
        {
          Header: "L-XL",
          accessor: "l-xl",
        },
        {
          Header: "XL-XXL",
          accessor: "xl-xxl",
        },
      ],
      []
    );

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data });
  

 
  return (
    <>
      <div className='menu'>
        <div className='container'>
          <button className="btn">PRODUCTS DETAILS</button>
          <button className="btn">REVIEWS</button>
          <button className="btn">SIZE & FIT</button>
          <button className="btn">SHIPPING & RETURNS</button>
        </div>
      </div>
              {/* ------------PRODUCTS DETAILS------------- */}
         <div className='content'>
            <div>
              <p>A little crisp, a little sunny, warm enough to skip a coat, just cool enough to wrap great cashmere around is this asymmetrical jumper. With a chic hue, this one makes for a perfect pick this fall. So
                  <br /> get your basics right and you are good to go.
              </p>

              <ul>
                <li>High-neck style</li>
                <li>Drop shoulders</li>
                <li>Flared cuffs</li>
                <li>Asymmetrical hem</li>
              </ul>
            </div>

            <div >
              <h3>MODEL MEASUREMENTS</h3>
